import { Subject } from 'rxjs';
export function componentDestroyed(component) {
  const oldNgOnDestroy = component.ngOnDestroy;
  const stop$ = new Subject();
  component.ngOnDestroy = function () {
    if (oldNgOnDestroy) {
      oldNgOnDestroy.apply(component);
    }
    stop$.next(undefined);
    stop$.complete();
  };
  return stop$;
}
