import * as layout from './layout.action';
import { updateObject } from '../../utils/update-object';
const initialState = {
  layout: 'alpha',
  sidenavOpen: true,
  sidenavCollapsed: false,
  sidenavAlign: 'start',
  sidenavMode: 'side',
  sidenavDisableClose: true,
  quickpanelOpen: false,
  layoutBoxed: false,
  settingsOpen: false,
  cardElevation: 'card-elevation-z1'
};
// Layouts
const layouts = {
  'alpha': {
    layout: 'alpha',
    sidenavOpen: true,
    sidenavCollapsed: true,
    sidenavMode: 'side',
    sidenavDisableClose: true,
    layoutBoxed: false
  },
  'beta': {
    layout: 'beta',
    sidenavOpen: true,
    sidenavCollapsed: false,
    sidenavMode: 'side',
    sidenavDisableClose: true,
    layoutBoxed: false
  },
  'gamma': {
    layout: 'gamma',
    sidenavOpen: false,
    sidenavCollapsed: false,
    sidenavMode: 'over',
    sidenavDisableClose: false,
    layoutBoxed: true
  }
};
export function reducer(state = initialState, action) {
  switch (action.type) {
    case layout.OPEN_SIDENAV:
      {
        return updateObject(state, {
          sidenavOpen: true
        });
      }
    case layout.CLOSE_SIDENAV:
      {
        return updateObject(state, {
          sidenavOpen: false
        });
      }
    case layout.TOGGLE_SIDENAV:
      {
        return updateObject(state, {
          sidenavOpen: !state.sidenavOpen
        });
      }
    case layout.ENABLE_SIDENAV_COLLAPSE:
      {
        return updateObject(state, {
          sidenavCollapsed: true
        });
      }
    case layout.DISABLE_SIDENAV_COLLAPSE:
      {
        return updateObject(state, {
          sidenavCollapsed: false
        });
      }
    case layout.TOGGLE_SIDENAV_COLLAPSE:
      {
        return updateObject(state, {
          sidenavCollapsed: !state.sidenavCollapsed
        });
      }
    case layout.SET_SIDENAV_ALIGN:
      {
        const mode = action.payload;
        return updateObject(state, {
          sidenavAlign: mode
        });
      }
    case layout.SET_SIDENAV_MODE:
      {
        const mode = action.payload;
        return updateObject(state, {
          sidenavMode: mode
        });
      }
    case layout.SET_SIDENAV_DISABLE_CLOSE:
      {
        const mode = action.payload;
        return updateObject(state, {
          sidenavDisableClose: mode
        });
      }
    case layout.OPEN_QUICKPANEL:
      {
        return updateObject(state, {
          quickpanelOpen: true
        });
      }
    case layout.CLOSE_QUICKPANEL:
      {
        return updateObject(state, {
          quickpanelOpen: false
        });
      }
    case layout.TOGGLE_QUICKPANEL:
      {
        return updateObject(state, {
          quickpanelOpen: !state.quickpanelOpen
        });
      }
    case layout.SELECT_LAYOUT:
      {
        const selectedLayout = action.payload;
        if (layouts[selectedLayout]) {
          return updateObject(state, layouts[selectedLayout]);
        }
        return updateObject(state, {
          layout: selectedLayout
        });
      }
    case layout.ENABLE_LAYOUT_BOXED:
      {
        return updateObject(state, {
          layoutBoxed: true
        });
      }
    case layout.DISABLE_LAYOUT_BOXED:
      {
        return updateObject(state, {
          layoutBoxed: false
        });
      }
    case layout.TOGGLE_LAYOUT_BOXED:
      {
        return updateObject(state, {
          layoutBoxed: !state.layoutBoxed
        });
      }
    case layout.OPEN_SETTINGS:
      {
        return updateObject(state, {
          settingsOpen: true
        });
      }
    case layout.CLOSE_SETTINGS:
      {
        return updateObject(state, {
          settingsOpen: false
        });
      }
    case layout.TOGGLE_SETTINGS:
      {
        return updateObject(state, {
          settingsOpen: !state.settingsOpen
        });
      }
    case layout.SET_CARD_ELEVATION:
      {
        const elevation = action.payload;
        return updateObject(state, {
          cardElevation: elevation
        });
      }
    default:
      return state;
  }
}
export const getSidenavOpen = state => state.sidenavOpen;
export const getSidenavCollapsed = state => state.sidenavCollapsed;
export const getSidenavAlign = state => state.sidenavAlign;
export const getSidenavMode = state => state.sidenavMode;
export const getSidenavDisableClose = state => state.sidenavDisableClose;
export const getQuickpanelOpen = state => state.quickpanelOpen;
export const getLayout = state => state.layout;
export const getLayoutBoxed = state => state.layoutBoxed;
export const getSettingsOpen = state => state.settingsOpen;
export const getCardElevation = state => state.cardElevation;
